import unittest
from suffix_tree_matching import SuffixTree, near_exact_pattern_matching as pattern_matching

def near_exact_pattern_matching(text, pattern):
    # tree = SuffixTree(text)
    matches = pattern_matching(text, pattern)
    return matches

